import "./TransactionForm.css";

function TransactionForm() {
    function sendOTP() {
        // console.log("sent");
        document.getElementById("sendOTPButton").disabled = True;
        function countdown() {
            var secondsLeft = 60;
            document.getElementById("sendOTPButton").textContent =
                "Resend OTP (" + countdown.toString() + "s)";
            countdown--;
            if (countdown < 0) {
                clearInterval(timer);
                document.getElementById("sendOTPButton").textContent =
                    "Send OTP";
            }
        }
        var otpInput = document.getElementById("sendOTPButton").textContent;
        var timer;
        timer = setInterval(countdown, 1000);
        console.log(otpInput);
    }
    function sendTokens() {
        // console.log("sent");
        var otpInput = document.getElementById("OTPField").value;
        console.log(otpInput);
        if (otpInput != "22") {
            document.getElementById("invalidOTP").style.display = "block";
        } else {
            //redirect to new page
        }
    }
    return (
        <div className="TransactionBackground">
            <div className="Form">
                <span className="FormTitle">Coin Transfer</span>
                <br></br>
                <hr className="FormDivider"></hr>
                <div className="requiredField">
                    <span className="asterick">*</span>
                    <input
                        type="text"
                        className="inputField"
                        placeholder="ETH Address"
                    ></input>
                </div>
                <div className="requiredField">
                    <span className="asterick">*</span>
                    <input
                        type="text"
                        className="inputField"
                        placeholder="Amount to send"
                    ></input>
                </div>
                <div className="requiredField">
                    <span className="asterick">*</span>
                    <input
                        type="text"
                        id="OTPField"
                        className="inputField OTPField"
                        placeholder="OTP Authentication"
                    ></input>
                    <button
                        id="sendOTPButton"
                        className="sendOTPButton"
                        onClick={sendOTP}
                    >
                        Send OTP
                    </button>
                </div>
                <span id="invalidOTP">Invalid OTP!</span>
                <div className="requiredField">
                    <input
                        type="text"
                        className="inputField remarksField"
                        placeholder="Remarks"
                    ></input>
                </div>
                <button className="submitButton">Send Tokens</button>
            </div>
        </div>
    );
}

export default TransactionForm;
